/* Loads a list of Cyclotron tags using jQuery.
   Could be done with the JSON Data Source as well.
 */
p = function (promise) {
    /* Start asychronous AJAX call */
    $.getJSON('http://cyclotrondev.karmalab.net/api/tags', function(data) {

        /* Resolves the result asynchronously via the provided promise */
        promise.resolve(_.map(data, function (tag) {
           return { 'name': tag }; 
        })); 
    });
}

/* Returns a list of browser features that Modernizr detected 
   http://modernizr.com/ 
 */
p = function(promise) {
    /* Convert from an object to a list of key/values */
    var pairs = _.pairs(Modernizr);

    var list = _.map(pairs, function (pair) {
        if (_.isFunction(pair[1])) { return null; }
        return { name: pair[0], value: pair[1] };
    })

    /* Return list synchronously */
    return _.compact(list);
}

/* Generates a list of prime numbers */
p = function(promise) {
    var isPrime = function (n) {
        for (var i = 2; i < n; i++) {
            if (n % i === 0) {
                return false;
            }
        }
        return true;
    };

    var result = [2];
    for (var i = 3; i < Cyclotron.parameters.maxPrime; i+=2) {
        if (isPrime(i)) {
            result.push(i);
        }
    }

    /* Return list synchronously */
    return _.map(result, function (prime, index) {
        return { prime: prime, index: index };
    });
}

/* Depends on the results from another Data Source.  This Data Source     
   triggers its dependency and waits for results using a promise.
 */
p = function (promise) {
    Cyclotron.dataSources['green-cities'].execute()
    .then(function(result) {
        /* Result contains the data for the dependency */
        /* '0' is the default ResultSet name */
        var data = result['0'].data;

        /* Resolve this data source asynchronously */
        promise.resolve(data);
    });
}

