/* Depends on the results from another Data Source.  This Data Source     
   waits for results from another Data Source using a promise.  This is
   ideal for depending on a Data Source which is executing independently.
 */
p = function (promise) {
    /* Get promise for the dependency result */
    var p = Cyclotron.dataSources['green-cities-preloaded'].getPromise();

    /* Attach a callback to the promise */
    p.then(function(result) {
        /* Result contains the data for the dependency */
        /* '0' is the default ResultSet name */
        var data = result['0'].data;

        /* Resolve this data source asynchronously */
        promise.resolve(data);
    });
}

